<?php	header("Cache-Control: no-store, no-cache, must-revalidate, no-transform, max-age=0, post-check=0, pre-check=0");	header("Pragma: no-cache");?><!DOCTYPE html><html><head><title>SMZ Message on a Bottle | ADMIN</title><link rel="stylesheet" type="text/css" href="<?=base_url()?>css/tooltipster.css" /><script type="text/javascript" src="<?=base_url()?>js/jquery-1.11.1.js"></script><script type="text/javascript" src="<?=base_url()?>js/jquery.tooltipster.js"></script><script type="text/javascript" src="<?=base_url()?>js/jquery.dataTables.js"></script><script>$(document).ready(function(){	//toggle gif approval	$(".approval").change(function() {		var gifid = $(this).attr('id');		var fbid = $(this).attr('alt');		if(this.checked) {			var val = 1;			$.post("<?=base_url()?>admin/approve_gif",{ fbid:fbid, gifid:gifid, val:val },function(data){},'json');		} else {			var val = 0;			$.post("<?=base_url()?>admin/approve_gif",{ fbid:fbid, gifid:gifid, val:val },function(data){},'json');		}	});		$('.mygif').mouseover(function(){		var gif = $(this).attr('href');		$(this).tooltipster({			position: 'right',			content: $('<span><img src="'+gif+'" width="400" /></span>')		});	});		//convert to datatable	$('#tblGallery').dataTable();});</script><style>body, html {	font-family: Candara;	font-size: 12px;	color: #3d3d3d;	position: absolute;	width: 100%;	top: 0;	left: 0;	margin: 0;}h1 {	margin-left: 20px;}#tblGallery td, #tblGallery th{	border: 2px solid #3d3d3d;}#tblGallery {	border-collapse: collapse;}#tblGallery th{	background-color: #3d3d3d;	color: #fff;	cursor: pointer;}#tblGallery_info {	text-align: center;}#tblGallery_paginate {	text-align: center;	margin-top: 30px;}#tblGallery_paginate a {	background-color: #3d3d3d;	color: #fff;	font-weight: bold;	padding: 5px;	margin: 3px;	cursor: pointer;}#tblGallery_length {	text-align: right;	width: 90%;	margin: 0 auto;}#tblGallery_filter {	text-align: left;	width: 90%;	margin: 0 auto;}a {	text-decoration: none;}</style></head><body><?php include "header.php"; ?><h1>Generated GIFs</h1><table align="center" cellpadding="3" cellspacing="0" id="tblGallery">	<thead>		<tr>			<th>#</th>			<th>Facebook ID</th>			<th>Name</th>			<th>GIF</th>			<th>Date Generated</th>			<th>Approved</th>		</tr>	</thead>	<tbody>		<?php $i = 1; foreach($gifs as $gif) { ?>		<tr>			<td><?=$i?></td>			<td><?=$gif->fbid?></td>			<td width="200"><?=$user->get_name($gif->fbid)?></td>			<td><a class="mygif tooltip" href="<?=base_url()?>images/uploads/<?=$gif->fbid?>/gif/<?=$gif->filename?>" target="_blank">view</a></td>			<td><?=date('F m, Y',strtotime($gif->date_generated))?></td>			<td align="center"><input type="checkbox" id="<?=$gif->id?>" alt="<?=$gif->fbid?>" class="approval" <?=($gif->approved == 1) ? "checked" : ""?> /></td>		</tr>		<?php $i++; } ?>	</tbody></table></body></html>