<?phpclass Activity_log extends CI_Model {	function insert($data) {		$this->db->insert('activity_log',$data);	}		function get_total_points($fbid) {		$query = $this->db->query("					SELECT SUM(points) AS total_points FROM activity_log					WHERE fbid = '".$fbid."'				");						foreach($query->result() as $logs) {			return $logs->total_points;		}	}		function get_activities($fbid) {		$this->db->where('fbid',$fbid);		$query = $this->db->get('activity_log');				return $query->result();	}	function delete_activity($fbid,$activity) {		$this->db->where('fbid',$fbid);		$this->db->where('activity',$activity);		$this->db->limit(1);		$this->db->delete('activity_log');	}}?>